INSERT INTO `wp_posts` VALUES(13, 1, '2012-02-23 12:42:24', '2012-02-23 20:42:24', 'AFC Finishing Systems was established by Carl Hagan Sr. in 1967 in Southern California as a provider of air filter products and spray booth maintenance. In 1974, the company added spray booths to its growing list of products, and in 2008 began to produce custom control panels for industrial applications. AFC Custom Controls continues to grow by expanding its product lines, customer bases, and using the most advanced technology in the industrial field.', 'About Us', '', 'publish', 'closed', 'closed', '', 'about', '', '', '2012-03-10 09:06:06', '2012-03-10 17:06:06', '', 0, 'http://afcindustrialcontrols.com/?page_id=13', 0, 'page', '', 0);
