INSERT INTO `wp_posts` VALUES(49, 1, '2012-03-03 15:21:55', '2012-03-03 23:21:55', 'This panel controls 5 dust control modules, each module is outfitted with a Photohelic which monitors filter loading once the loading reaches the user defined set point it sends a signal back to the control panel which will start the blown down/cleaning process of the filters. This panel also has and end of shift button that when pressed will clean the filters and then shut its self off.', 'DCP', '', 'inherit', 'closed', 'open', '', '28-autosave', '', '', '2012-03-03 15:21:55', '2012-03-03 23:21:55', '', 28, 'http://afcindustrialcontrols.com/28-autosave/', 0, 'revision', '', 0);
