INSERT INTO `wp_posts` VALUES(54, 1, '2012-02-23 12:55:01', '2012-02-23 20:55:01', 'AFC Finishing Systems was established by Carl Hagan Sr. in 1967 in Southern California as a provider of air filter products and spray booth maintenance. In 1974, the company added spray booths to its growing list of products, and in 2008 began to produce custom control panels for industrial applications. AFC Custom Controls continues to grow by expanding its product lines, customer bases, and using the most advanced technology in the industrial field.', 'About Us', '', 'inherit', 'closed', 'open', '', '13-revision-3', '', '', '2012-02-23 12:55:01', '2012-02-23 20:55:01', '', 13, 'http://afcindustrialcontrols.com/13-revision-3/', 0, 'revision', '', 0);
