INSERT INTO `wp_posts` VALUES(59, 1, '2012-03-10 11:19:14', '2012-03-10 19:19:14', 'Fully automated control panel with Siemens 6” color touch screen and PLC, with a telephone modem and/or Ethernet card that is incorporated into the control box so that AFC can offer remote assistance and troubleshooting. This control panel is controlling one Air Make-Up units, and monitoring pressure inside of the booth. It also data logs the incoming temperature, outgoing temperature, humidity, what cycle it is in, and how long it is in each cycle. This data is stored for three months at a time and is easily downloaded to a USB stick via the USB port located on the front of the control panel. This box has customer supplied logo and graphics incorporated into the touch panel for a private label look.', '700TS', '', 'inherit', 'closed', 'open', '', '27-autosave', '', '', '2012-03-10 11:19:14', '2012-03-10 19:19:14', '', 27, 'http://afcindustrialcontrols.com/27-autosave/', 0, 'revision', '', 0);
